$(function () {
    // When the DOM is fully loaded and ready, run this function
    $("*[include-html]").each(function () {
      // For each element with the 'include-html' attribute, do the following:
      
      var url = $(this).attr("include-html"); // Get the URL from the 'include-html' attribute
      var id = this.id; // Get the ID of the current element
  
      // Perform an AJAX request to fetch content from the URL
      jQuery.ajax({
        url: url, // URL to fetch content from
        success: function (response) {
          // If the request is successful:
          $("#" + id).html(response); // Insert the fetched content into the element with the ID
  
          // Trigger a 'load' event to let other scripts know that content has been loaded
          var event = new Event('load'); // Create a new 'load' event
          window.dispatchEvent(event); // Dispatch the 'load' event on the window object
        },
        error: function (xhr, status, error) {
          // If there is an error with the request:
          var errorMessage = xhr.status + ": " + xhr.statusText; // Create an error message with status code and text
          $("#" + id).html(errorMessage); // Display the error message in the element
        }
      });
    });
  });
  